// src/components/BirthsBarChart.js

import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const BirthsBarChart = ({ data, municipalities, years, genders }) => {
    const chartData = {
        labels: years,
        datasets: municipalities.flatMap(municipality => {
            const item = data.find(d => d.municipality === municipality);
            return [
                {
                    label: `Males - ${municipality}`,
                    data: years.map(year => item.births[year].male),
                    backgroundColor: 'rgba(75, 192, 192, 0.2)',
                    borderColor: 'rgba(75, 192, 192, 1)',
                    borderWidth: 1,
                },
                {
                    label: `Females - ${municipality}`,
                    data: years.map(year => item.births[year].female),
                    backgroundColor: 'rgba(255, 99, 132, 0.2)',
                    borderColor: 'rgba(255, 99, 132, 1)',
                    borderWidth: 1,
                },
            ];
        }),
    };

    const options = {
        responsive: true,
        plugins: {
            legend: {
                position: 'top',
            },
            tooltip: {
                callbacks: {
                    label: function (context) {
                        let label = context.dataset.label || '';
                        if (context.parsed.y !== null) {
                            label += `: ${context.parsed.y}`;
                        }
                        return label;
                    },
                },
            },
        },
    };

    return <Bar data={chartData} options={options} />;
};

export default BirthsBarChart;
