import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [births, setBirths] = useState([]);
  const [muncipalitySelected, setMuncipalitySelected] = useState([]);
  const [yearSelected, setYearSelected] = useState([]);
  const [genderSelected, setGenderSelected] = useState([]);
  const [showTable, setShowTable] = useState(true);

  // Initialize theme state based on localStorage or default to 'dark'
  const [theme, setTheme] = useState(() => {
    const savedTheme = localStorage.getItem('theme');
    return savedTheme ? savedTheme : 'dark';
  });

  useEffect(() => {
    // Fetch data 
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/births');
        setBirths(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
    fetchData();

    // Visual Setting Dark/Light theme
    localStorage.setItem('theme', theme);
    document.body.className = theme; // Apply theme class to body


  }, [theme]);

  // Toggle theme function
  const toggleTheme = () => {
    setTheme(prevTheme => (prevTheme === 'light' ? 'dark' : 'light'));
  };

  // Show Table function
  const handleShowTableClick = () => {
    setShowTable(true);
  };

  const handleMuncipalityChange = (e) => {
    setMuncipalitySelected([...e.target.selectedOptions].map(option => option.value));
  };

  const handleYearChange = (e) => {
    setYearSelected([...e.target.selectedOptions].map(option => option.value));
  };

  const handleGenderChange = (e) => {
    setGenderSelected([...e.target.selectedOptions].map(option => option.value));
  };

  const areAllOptionsSelected = () => {
    return muncipalitySelected.length > 0 && yearSelected.length > 0 && genderSelected.length > 0;
  };

  // Show BarChart function
  const handleHorizontalBarChart = () => {
    setShowTable(false);
  };

  // Show BarChart function
  const handleVerticalBarChart = () => {
    setShowTable(false);
  };

  return (
    <div className="App">
      <h1>Birth Statistics by Municipality (2016-2020)</h1>
      <p>
        Welcome to our birth statistics dashboard! The data presented here is sourced from the Statistics Sweden (SCB) database,
        which is the official government agency responsible for producing official statistics in Sweden. The SCB is a reliable
        and authoritative source, providing accurate and comprehensive data on various aspects of Swedish society.
        Here, you will find detailed information on the number of births from 2016 to 2020, broken down by municipality and gender.
        We hope you find this information useful and insightful.
      </p>
      {/* START: Options Area */}
      <div className="options-container">
        <div className="rectangle-box">
          <label htmlFor="dropdowns" className="options-label"> Select Options to Compare Data</label>
          <div className="dropdown-group">
            {/* Select Muncipalities to compare */}
            <div className="dropdown-wrapper">
              <label htmlFor="municipalities-dropdown" className="dropdown-label">Select Municipalities:</label>
              <select multiple className="dropdown" onChange={handleMuncipalityChange}>
                <option value="" disabled selected>Select Options</option>
                {births.map((item) => (
                  <option value={item.municipality}>{item.municipality}</option>
                ))}
              </select>
            </div>

            {/* Select Years to compare */}
            <div className="dropdown-wrapper">
              <label htmlFor="municipalities-dropdown" className="dropdown-label">Select Years:</label>
              <select multiple className="dropdown" onChange={handleYearChange}>
                <option value="" disabled selected>Select Options</option>
                <option value="option1">2016</option>
                <option value="option2">2017</option>
                <option value="option3">2018</option>
                <option value="option2">2019</option>
                <option value="option3">2020</option>
              </select>
            </div>
            {/* Select Gender to compare */}
            <div className="dropdown-wrapper">
              <label htmlFor="municipalities-dropdown" className="dropdown-label">Select Gender:</label>
              <select multiple className="dropdown" onChange={handleGenderChange}>
                <option value="" disabled selected>Select Options</option>
                <option value="option1">Male</option>
                <option value="option2">Female</option>
              </select>
            </div>
            <div className="button-container">
              <button disabled={!areAllOptionsSelected()} onClick={handleHorizontalBarChart}>Show Horizontal Bar Charts</button>
              <button disabled={!areAllOptionsSelected()} onClick={handleVerticalBarChart}>Show Vertical Bar Charts</button>
            </div>
          </div>
        </div>
        <button disabled={showTable} onClick={handleShowTableClick}>Show all data in one Table</button>
        <button onClick={toggleTheme}> Switch to {theme === 'light' ? 'Dark' : 'Light'} Mode </button>
      </div>
      {/* END: Options Area */}

      {/* START: Data in Table View */}
      {
        showTable && (<table>
          <thead>
            <tr>
              <th>Municipality</th>
              <th colSpan={2}>2016</th>
              <th colSpan={2}>2017</th>
              <th colSpan={2}>2018</th>
              <th colSpan={2}>2019</th>
              <th colSpan={2}>2020</th>
            </tr>
            <tr>
              <th></th>
              <th className="males">Males</th>
              <th className="females">Females</th>
              <th className="males">Males</th>
              <th className="females">Females</th>
              <th className="males">Males</th>
              <th className="females">Females</th>
              <th className="males">Males</th>
              <th className="females">Females</th>
              <th className="males">Males</th>
              <th className="females">Females</th>
            </tr>
          </thead>
          <tbody>
            {births.map((item) => (
              <tr key={item.municipality}>
                <td className="left-align" >{item.municipality}</td>
                <td className="males">{item.births[2016].male}</td>
                <td className="females">{item.births[2016].female}</td>
                <td className="males">{item.births[2017].male}</td>
                <td className="females">{item.births[2017].female}</td>
                <td className="males">{item.births[2018].male}</td>
                <td className="females">{item.births[2018].female}</td>
                <td className="males">{item.births[2019].male}</td>
                <td className="females">{item.births[2019].female}</td>
                <td className="males">{item.births[2020].male}</td>
                <td className="females">{item.births[2020].female}</td>
              </tr>
            ))}
          </tbody>
        </table>)
      }
      {/* END: Data in Table View */}
    </div >
  );
}

export default App;
